const channel = {
    volume: 0,
    mute: false
  }

  const synth = {
      volume: 0,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'sawtooth',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }

    const sequence = {
      steps: [
        {
          time: '0:0:0',
          noteName: 'C1',
          duration: '10n',
          velocity: 1
        },
        {
          time: '1:0:0',
          noteName: 'B1',
          duration: '10n',
          velocity: 1
        },
        {
          time: '2:0:0',
          noteName: 'A1',
          duration: '5n',
          velocity: 1
        },
        {
          time: '3:0:0',
          noteName: 'A1',
          duration: '5n',
          velocity: 1
        }
      ],
      duration: '1t'
    }

    const title = "Third"

    export { synth, sequence, title, channel }
