import React, { Component } from 'react'
import * as Tone from 'tone'
import * as firstSettings from '../tunes/firstDrum'
import SC_Button from '../components/SC_Button'

let firstSynth

export default class Drums extends Component {
    constructor(props) {
      super(props)

      this.state = {
        firstSettings
      }
    }

    handleDrumsStart = () => {
        const { firstSettings } = this.state
        //
        firstSynth = new Tone.Synth(firstSettings.synth)

        const firstPart = new Tone.Part((time, note) => {
          firstSynth.triggerAttackRelease(
            note.noteName,
            note.duration,
            time,
            note.velocity
          )
        }, firstSettings.sequence.steps).start(0)

        firstPart.loopEnd = firstSettings.sequence.duration
        firstPart.loop = true

        const channelFirstSettings = { volume: 0, pan: 0, mute: false, solo: false }
        const channelFirst = new Tone.Channel(channelFirstSettings).toDestination()
        firstSynth.chain(channelFirst)
        //

        Tone.Transport.start()
      }


    render(){

      const { firstSettings } = this.state

      return (
        <div className="Drums">
        <SC_Button
            text="â–¶"
            handleClick={this.handleDrumsStart}
          />
        </div>
      )

    }
}
