const channel = {
  volume: 0,
  mute: true
}

const synth = {
    volume: -5,
    detune: 0,
    portamento: 0.05,
    envelope: {
      attack: 0.05,
      attackCurve: 'exponential',
      decay: 0,
      decayCurve: 'exponential',
      sustain: 0,
      release: 1.5,
      releaseCurve: 'exponential'
    },
    oscillator: {
      type: 'triangle',
      modulationType: 'sine',
      // partialCount: 0,
      // partials: [],
      phase: 0,
      harmonicity: 0.5
    }
  }

  const pingPongDelay = { wet: 0.2, delayTime: 0.25, maxDelayTime: 1 }

  const sequence = {
    steps: [
      {
        time: '0:0:0',
        noteName: 'A3',
        duration: '1n',
        velocity: 1
      },
      {
        time: '1:0:0',
        noteName: 'A1',
        duration: '1n',
        velocity: 1
      },
      {
        time: '2:0:0',
        noteName: 'A3',
        duration: '1n',
        velocity: 1
      },
      {
        time: '3:0:0',
        noteName: 'A4',
        duration: '1n',
        velocity: 1
      }
    ],
    duration: '4n'
  }

  const title = "Beep"

  export { synth, sequence, title, channel }
