const synth = {
  volume: 0,
  detune: 0,
  portamento: 0.05,
  envelope: {
    attack: 0.05,
    attackCurve: 'exponential',
    decay: 0.2,
    decayCurve: 'exponential',
    sustain: 0.2,
    release: 1.5,
    releaseCurve: 'exponential'
  },
  oscillator: {
    type: 'square',
    modulationType: 'sine',
    // partialCount: 0,
    // partials: [],
    phase: 0,
    harmonicity: 0.5
  }
}

const chorus = {
  wet: 0.3,
  type: 'sine',
  frequency: 1.5,
  delayTime: 3.5,
  depth: 0.7,
  spread: 180
}

// const pingPongDelay = { wet: 0.2, delayTime: 0.25, maxDelayTime: 1 }

const sequence = {
  steps: [
    {
      time: '0:0:0',
      noteName: 'C2',
      duration: '10n',
      velocity: 1
    },
    {
      time: '0:0:1',
      noteName: 'E2',
      duration: '10n',
      velocity: 1
    },
    {
      time: '0:0:2',
      noteName: 'C2',
      duration: '5n',
      velocity: 1
    }
  ],
  duration: '3t'
}

const title = "Main Part"

export { synth, chorus, sequence, title }
