import * as Tone from 'tone'
import React, { Component } from 'react'

import * as seaSettings from './tunes/sea.js'

import SC_Button from './components/SC_Button'

let seaSynth
let seaChorus
let seaPingPongDelay

let beatSynth

export default class Container extends Component {
  constructor(props) {
    super(props)

    this.state = {
      seaSettings
    }
  }

  handleStart = () => {
    const { seaSettings } = this.state
    //
    seaSynth = new Tone.Synth(seaSettings.synth)
    seaChorus = new Tone.Chorus(seaSettings.chorus).start()
    seaPingPongDelay = new Tone.PingPongDelay(
      seaSettings.pingPongDelay
    ).toDestination()
    seaSynth.chain(seaChorus, seaPingPongDelay)
    const seaPart = new Tone.Part((time, note) => {
      seaSynth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, seaSettings.sequence.steps).start(0)
    seaPart.loopEnd = seaSettings.sequence.duration
    seaPart.loop = true
    Tone.Transport.start()
  }

  handleBeatSrart = () => {
    const { beatSettings } = this.state
    //
    beatSynth = new Tone.Synth(beatSettings.synth)

    const seaPart = new Tone.Part((time, note) => {
      beatSynth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, beatSettings.sequence.steps).start(0)
    beatPart.loopEnd = beatSettings.sequence.duration
    beatPart.loop = true
    Tone.Transport.start()
  }

  render() {
    const { beatSettings } = this.state

    return (
      <div className="Container">
        <SC_Button
          text=">start<"
          handleClick={this.handleStart}
        />
        <SC_Button
          text=">start<"
          handleClick={this.handleBeatSrart}
        />
      </div>
    )
  }
}
