import * as Tone from 'tone'
import React, { Component } from 'react'

import * as seaSettings from './tunes/sea.js'
import * as melodySettings from './tunes/calm.js'

import SC_Button from './components/SC_Button'
import SC_Slider from './components/SC_Slider'

let seaSynth
let seaChorus
let seaPingPongDelay


export default class Container extends Component {
  constructor(props) {
    super(props)

    this.state = {
      seaSynth
    }
  }

  handleStart = () => {
    const { seaSynth } = this.state

    //
    seaSynth = new Tone.Synth(seaSettings.synth)

    const seaChannelSettings = { volume: 0, pan: 0, mute: false, solo: false }
    const seaChannel = new Tone.Channel(seaChannelSettings).toDestination()

    seaSynth.chain(seaChannel)

    const seaPart = new Tone.Part((time, note) => {
      seaSynth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, bassSettings.sequence.steps).start(0)

    seaPart.loopEnd = bassSettings.sequence.duration
    seaPart.loop = true
    //

    Tone.Transport.start()
  }

  render() {
    const { seaSettings } = this.state

    return (
      <div className="Container">
        <SC_Button
          text="Art Design & Coding Community"
          handleClick={this.handleStart}
        />
      </div>
    )
  }
}
